INSERT INTO `wp_snippets` VALUES (1,'SUGO Customizer - Contact Section','This adds the Contact Section to the Home Sections in the SUGO theme Customizer.','// BEGIN CUSTOMIZER\r\n\r\nfunction sugo_customize_register( $wp_customize )\r\n{  \r\n  \r\n  //SLIDERS\r\n	for ( $i=1; $i < 6; $i++ ){\r\n\r\n		$wp_customize->add_setting(\r\n			\'total_slider_heading\'.$i,\r\n			array(\r\n				\'sanitize_callback\' => \'total_sanitize_text\'\r\n			)\r\n		);\r\n\r\n		$wp_customize->add_control(\r\n			new Total_Customize_Heading(\r\n				$wp_customize,\r\n				\'total_slider_heading\'.$i,\r\n				array(\r\n					\'settings\'		=> \'total_slider_heading\'.$i,\r\n					\'section\'		=> \'total_slider_section\',\r\n					\'label\'			=> __( \'Slider \', \'total\' ).$i,\r\n				)\r\n			)\r\n		);\r\n\r\n		$wp_customize->add_setting(\r\n			\'total_slider_page\'.$i,\r\n			array(\r\n				\'sanitize_callback\' => \'absint\'\r\n			)\r\n		);\r\n\r\n		$wp_customize->add_control(\r\n			\'total_slider_page\'.$i,\r\n			array(\r\n				\'settings\'		=> \'total_slider_page\'.$i,\r\n				\'section\'		=> \'total_slider_section\',\r\n				\'type\'			=> \'dropdown-pages\',\r\n				\'label\'			=> __( \'Select a Page\', \'total\' ),	\r\n			)\r\n		);\r\n		\r\n	}\r\n  \r\n  \r\n   	$wp_customize->add_section(\r\n		\'sugo_contact_section\',\r\n		array(\r\n			\'title\' 			=> __( \'Contact Section\', \'sugo\' ),\r\n			\'panel\'     		=> \'total_home_panel\'\r\n		)\r\n	);\r\n\r\n	//ENABLE/DISABLE CONTACT PAGE\r\n	$wp_customize->add_setting(\r\n		\'sugo_contact_page_disable\',\r\n		array(\r\n			\'sanitize_callback\' => \'total_sanitize_text\', //method from parent theme\r\n			\'default\' => \'off\'\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n		new Total_Switch_Control( //class from parent theme\r\n			$wp_customize,\r\n			\'sugo_contact_page_disable\',\r\n			array(\r\n				\'settings\'		=> \'sugo_contact_page_disable\',\r\n				\'section\'		=> \'sugo_contact_section\',\r\n				\'label\'			=> __( \'Disable Section\', \'sugo\' ),\r\n				\'on_off_label\' 	=> array(\r\n					\'on\' => __( \'Yes\', \'sugo\' ),\r\n					\'off\' => __( \'No\', \'sugo\' )\r\n					)	\r\n			)\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_setting(\r\n		\'sugo_contact_page\',\r\n		array(\r\n			\'sanitize_callback\' => \'absint\',\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n		\'sugo_contact_page\',\r\n		array(\r\n			\'settings\'		=> \'sugo_contact_page\',\r\n			\'section\'		=> \'sugo_contact_section\',\r\n			\'type\'			=> \'dropdown-pages\',\r\n			\'label\'			=> __( \'Select a Page\', \'sugo\' ),	\r\n		)\r\n	);\r\n\r\n    $wp_customize->add_setting(\r\n		\'sugo_contact_image_heading\',\r\n		array(\r\n			\'sanitize_callback\' => \'total_sanitize_text\' //method from parent theme\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n		new Total_Customize_Heading( //class from parent theme\r\n			$wp_customize,\r\n			\'sugo_contact_image_heading\',\r\n			array(\r\n				\'settings\'		=> \'sugo_contact_image_heading\',\r\n				\'section\'		=> \'sugo_contact_section\',\r\n				\'label\'			=> __( \'Right Image\', \'sugo\' ),\r\n			)\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_setting(\r\n		\'sugo_contact_image\',\r\n		array(\r\n			\'sanitize_callback\' => \'esc_url_raw\'\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n		new WP_Customize_Image_Control(\r\n			$wp_customize,\r\n		    \'sugo_contact_image\',\r\n		    array(\r\n		        \'section\' => \'sugo_contact_section\',\r\n		        \'settings\' => \'sugo_contact_image\',\r\n		        \'description\' => __(\'Recommended Image Size: 500X600px\', \'sugo\')\r\n		    )\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_setting(\r\n		\'sugo_contact_widget\',\r\n		array(\r\n			\'default\'			=> \'0\',\r\n			\'sanitize_callback\' => \'total_sanitize_choices\' //method from parent theme\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n		\'sugo_contact_widget\',\r\n		array(\r\n			\'settings\'		=> \'sugo_contact_widget\',\r\n			\'section\'		=> \'sugo_contact_section\',\r\n			\'type\'			=> \'select\',\r\n			\'label\'			=> __( \'Replace Image by widget\', \'sugo\' ),\r\n			\'choices\'       => $total_widget_list //variable from parent theme\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_setting(\r\n		\'sugo_contact_bg\',\r\n		array(\r\n			\'sanitize_callback\' => \'esc_url_raw\',\r\n			\'default\'			=> get_template_directory_uri().\'/images/contact-banner.jpg\'\r\n		)\r\n	);\r\n\r\n	$wp_customize->add_control(\r\n	    new WP_Customize_Image_Control(\r\n	        $wp_customize,\r\n	        \'sugo_contact_bg\',\r\n	        array(\r\n	            \'label\' => __( \'Background Image\', \'sugo\' ),\r\n	            \'section\' => \'sugo_contact_section\',\r\n	            \'settings\' => \'sugo_contact_bg\',\r\n	            \'description\' => __(\'Recommended Image Size: 1800X800px\', \'sugo\')\r\n	        )\r\n	    )\r\n	);\r\n}\r\nadd_action( \'customize_register\', \'sugo_customize_register\' );\r\n\r\n// END CUSTOMIZER\r\n\r\n\r\n\r\nfunction sugo_dynamic_styles(){\r\n	echo \"<style>\";\r\n	$sugo_contact_bg = get_theme_mod(\'sugo_contact_bg\');\r\n	echo \'#ht-contact-section{ background-image:url(\' .esc_url($sugo_contact_bg). \');}\';\r\n	echo \"</style>\";\r\n}\r\n\r\nadd_action( \'wp_head\', \'sugo_dynamic_styles\' );','','global',1),(3,'Enqueue Sign In Styles','Enqueue custom sign in styles for the login form.','// load css into the login page\r\nfunction sugo_custom_signin_styles() {\r\n    wp_enqueue_style( \'sugo-signin-style\', get_stylesheet_directory_uri() . \'/css/signin.css\' ); \r\n}\r\nadd_action( \'login_enqueue_scripts\', \'sugo_custom_signin_styles\' );','','global',0),(4,'Remove Parent Theme\'s Inline Styles','Used to remove the custom inline styles from the \"Total\" theme.','function wpse_remove_inline_style( $handler )\r\n{\r\n       wp_style_is( $handler, \'enqueued\' ) \r\n    && wp_style_add_data( \'total-style\', \'after\', \'\' );\r\n}\r\n\r\nadd_action( \'wp_print_styles\', function()\r\n{\r\n    // Remove previous inline style\r\n    wpse_remove_inline_style( \'total-style\' );    \r\n\r\n} );','','global',1),(5,'Logged In/Out Menus','This will show different menus depending on if the user is logged in or not.','function my_wp_nav_menu_args( $args = \'\' ) {\r\n \r\nif( is_user_logged_in() ) { \r\n    $args[\'menu\'] = \'Logged In\';\r\n} else { \r\n    $args[\'menu\'] = \'Logged Out\';\r\n} \r\n    return $args;\r\n}\r\nadd_filter( \'wp_nav_menu_args\', \'my_wp_nav_menu_args\' );','','global',1),(10,'SUGO Custom JS','Custom javascript for SUGO theme.','add_action(\'init\', \'sugo_custom_init\');\r\n\r\nfunction sugo_custom_init() {\r\nwp_deregister_script(\'total-custom\');\r\nwp_enqueue_script(\'sugo-custom\', get_stylesheet_directory_uri() . \'/js/sugo-custom.js\', array( \'jquery\' ),\'\', true  );\r\n}\r\n\r\nadd_action( \'wp_enqueue_scripts\', \'sugo_custom_init\', 100 );\r\n','','global',1),(8,'SUGO Customizer Scripts','Child theme versions of parent scripts.','/*\r\n  Removing the action from the parent theme and adding an action from the child theme. \r\n*/\r\nadd_action(\'init\', \'sugo_customizer_init\');\r\n\r\nfunction sugo_customizer_init() {\r\nremove_action( \'customize_controls_enqueue_scripts\', \'total_customizer_script\' );\r\nadd_action(\'customize_controls_enqueue_scripts\', \'sugo_customizer_script\');\r\n}\r\n\r\nfunction sugo_customizer_script() {\r\n	wp_enqueue_script( \'sugo-customizer-script\', get_stylesheet_directory_uri() .\'/inc/js/sugo-customizer-scripts.js\', array(\"jquery\"),\'\', true  );\r\n	wp_enqueue_script( \'total-customizer-chosen-script\', get_template_directory_uri() .\'/inc/js/chosen.jquery.js\', array(\"jquery\"),\'1.4.1\', true  );\r\n	wp_enqueue_style( \'font-awesome\', get_template_directory_uri() .\'/css/font-awesome.css\');	\r\n	wp_enqueue_style( \'total-customizer-chosen-style\', get_template_directory_uri() .\'/inc/css/chosen.css\');\r\n	wp_enqueue_style( \'total-customizer-style\', get_template_directory_uri() .\'/inc/css/customizer-style.css\');	\r\n}','','global',1);
